mapper.select:

SELECT *
  FROM (SELECT R.*
					  , rownum AS rn
				FROM (SELECT * FROM travle_review_tbl
						  WHERE r_show = 'Y'
						  ORDER BY r_readCnt DESC) R
			)
 WHERE rn >= #{start}
  	 AND rn <= #{end}

UPDATE travle_review_tbl
   SET r_readCnt = r_readCnt +1
 WHERE r_num = #{r_num}
